package org.jellyfin.openapi.constants

object Strings {
	/**
	 * Warning that is inserted at the top of all generated files.
	 */
	val FILE_TOP_WARNING = """
		|!!        WARNING
		|!! DO NOT EDIT THIS FILE
		|
		|This file is generated by the openapi-generator module and is not meant for manual changes.
		|Please read the README.md file in the openapi-generator module for additional information.
	""".trimMargin()

	/**
	 * Message of the @Deprecation warning for a member (function or property).
	 */
	const val DEPRECATED_MEMBER = "This member is deprecated and may be removed in the future"

	/**
	 * Message of the @Deprecation warning for a class (includes enums).
	 */
	const val DEPRECATED_CLASS = "This class is deprecated and may be removed in the future"

	/**
	 * The default service name for API operations.
	 */
	const val DEFAULT_API_SERVICE = "Misc"

	/**
	 * The suffix added to the name of a deprecated operation.
	 */
	const val DEPRECATED_OPERATION_SUFFIX = "Deprecated"

	/**
	 * The suffix added to the name of a URL operation. Added after [URL_OPERATION_SUFFIX].
	 */
	const val URL_OPERATION_SUFFIX = "Url"

	/**
	 * The suffix added to the name of a service containing API operations.
	 */
	const val API_SERVICE_SUFFIX = "Api"

	/**
	 * Name of the parameter used to reference the HttpClient instance in generated api classes.
	 */
	const val API_CLIENT_PARAMETER_NAME = "api"

	/**
	 * The suffix added to generated models containing operation parameters.
	 */
	const val MODEL_REQUEST_SUFFIX = "Request"

	/**
	 * Name of the parameter used for the generated request model containing operation parameters.
	 */
	const val MODEL_REQUEST_PARAMETER_NAME = "request"

	/**
	 * Description of the parameter used for the generated request model containing operation parameters.
	 */
	const val MODEL_REQUEST_PARAMETER_DESCRIPTION = "The request parameters"

	/**
	 * Message used in the exception thrown when the serialName is invalid. Use $serialName for the input value.
	 */
	const val MODEL_ENUM_MEMBER_EXCEPTION_MESSAGE = "Unknown value \$serialName"

	/**
	 * Function description for the `toString` function of generated enums.
	 */
	const val MODEL_ENUM_TO_STRING_DESCRIPTION = "Get the serial name of the enum member."

	/**
	 * Function description for the `fromName` function of generated enums.
	 */
	const val MODEL_ENUM_FROM_NAME_DESCRIPTION = "Find the enum member by the serial name or throw."

	/**
	 * Function description for the `fromNameOrNull` function of generated enums.
	 */
